import { useContext, useState } from "react";
import { MovieContext } from "../../context/Apis";

const FormSearch = () => {
  const getPosterUrl = (posterPath) => {
    return `https://image.tmdb.org/t/p/original${posterPath}`;
  };

  const [movieSearch, setMovieSearch] = useState("");
  const [filteredMovie, setFilteredMovie] = useState([]);
  const { movies } = useContext(MovieContext);

  const handleChange = (ev) => {
    const searchTitle = ev.target.value;
    setMovieSearch(searchTitle);
    if (searchTitle === "") {
      setFilteredMovie([]);
    }
  };

  const handleSearch = (ev) => {
    ev.preventDefault();
    filter(movieSearch);
  };

  const filter = (searchTitle) => {
    const filteredMovies = movies.filter((movie) =>
      movie.title.toString().toLowerCase().includes(searchTitle.toLowerCase())
    );
    setFilteredMovie(filteredMovies);
  };

  return (
    <div>
      <form onSubmit={handleSearch}>
        <input
          type="text"
          placeholder="Enter title"
          value={movieSearch}
          onChange={handleChange}
        />
        <button type="submit">Search</button>
      </form>
      {filteredMovie.length > 0 ? (
        filteredMovie.map((movie) => (
          <div key={movie.id}>
            <h3>{movie.title}</h3>
            <img
              src={getPosterUrl(movie.poster_path)}
              alt={movie.original_title}
            />
          </div>
        ))
      ) : (
        <div>
          {movies.map((movie) => (
            <div key={movie.id}>
              <h3>{movie.title}</h3>
              <img
                src={getPosterUrl(movie.poster_path)}
                alt={movie.original_title}
              />
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default FormSearch;
